let bigAdd l1 l2 =
  let add (l1,l2) =
    let f a x =
      match x with
      | (m,n) ->
          let union = m + n in
          (match a with
           | [] -> [union / 10; union mod 10]
           | h::t -> [(union + h) / 10] @ ([(union + h) mod 10] @ t)) in
    let base = [] in
    let args = List.rev (List.combine l1 l2) in
    let res = List.fold_left f base args in res in
  removeZero (add (padZero l1 l2));;

