let rec clone x n = 
  if n < 1 then []
  else x :: clone x (n-1)


let _ = clone 3 5;;
let _ = clone "foo" 2;; 
let _ = clone clone (-3);;


let padZero l1 l2 = let d = List.length l1 - List.length l2 in
    if d = 0 then (l1,l2) 
    		    else if d < 0 then ((clone 0 (d * -1)) @ l1, l2)
    		    else (l1, (clone 0 d) @ l2)


let _ = padZero [9;9] [1;0;0;2]
let _ = padZero [1;0;0;2] [9;9] 


let rec removeZero l = match l with
  | [] -> []
  | h :: t -> if h = 0 then removeZero t
      else l


