let _ = removeZero [9;9]
let _ = removeZero [0;0;0;0]


let bigAdd l1 l2 = 
  let add (l1, l2) = 
    let f a x = match x with 
      |(m,n) -> let union = m + n 
          in match a with
            	     | [] -> [union/10 ; union mod 10]
            	     | h::t ->   [(union+h)/10] @ [(union+h) mod 10] @ t
    in
    let base = [] in
    let args = List.rev (List.combine l1 l2) in
    let  res = List.fold_left f base args in
      res
  in 
    removeZero (add (padZero l1 l2))


let _ = bigAdd [9;9] [1;0;0;2];;
let _ = bigAdd [9;9;9;9] [9;9;9];; 



let rec mulByDigit i l = 
  if i < 2 then l
  else
    let f a x = match a with
      | [] -> [(x * i) / 10; (x * i) mod 10]
      	 | h :: t -> [(x*i+h)/10; (x*i+h) mod 10] @ t
    in let base = [] in
    let args = List.rev l in
      List.fold_left f base args


let _ = mulByDigit 9 [9;9;9;9]


let bigMul l1 l2 = 
  let f a x = match a with
    |(n,l) -> match l with
      | [] -> (n+1, mulByDigit x l1)
      	   | h::t -> (n+1, bigAdd l ((mulByDigit x l1) @ (clone 0 n)))
  in
  let base = (0,[]) in
  let args =  List.rev l2 in
  let (_, res) = List.fold_left f base args in
    res



let _ = bigMul [9;9;9;9] [9;9;9;9]
let _ = bigMul [9;9;9;9;9] [9;9;9;9;9] 







