let padZero l1 l2 = 
  if ((List.length l1) < (List.length l2)) then 
    let n = ((List.length l2) - (List.length l1)) in ((clone 0 n)@l1, l2)
  else if ((List.length l2) < (List.length l1)) then 
    let n = ((List.length l1) - (List.length l2)) in (l1,(clone 0 n)@l2)
  else (l1, l2)

