let sqsum xs = 
let f a x = a + (x*x) in (* a is our 'pocket', added to new x^2 *)
let base = 0 in	         (* base case = 0 *)
List.fold_left f base xs

let pipe fs = 
let f a x = (let func(y) = x(a y) in func) in
let base = (let func(y) = y in func) in	(* return y, just the value we have *)
List.fold_left f base fs

let rec sepConcat sep sl = match sl with 
| [] -> ""
| h :: t -> 
let f a x =  a ^ sep ^ x in (* a is what we have concatenated, x is next to concat *)
let base = h in (* if no more to concat, add on last elemet (head) *)
let l = t in    (* fold left by recursing with the tail each round *)
List.fold_left f base l

