let rec foldr op base xs = match xs with
  | []     -> base
  | x::xs' -> op x (foldr op base xs')
;;

let sqsum xs = 
  let f a x = a + (x*x) in (*XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX*)
  let base = 0 in	         (*XXXXXXXXXXXXXXX*)
    List.fold_left f base xs

