let pipe fs = 
  let f a x = (let func(y) = x(a y) in func) in
  let base = (let func(y) = y in func) in	(*XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX*)
    List.fold_left f base fs


