XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX
*)
let rec mulByDigit i l = 
  if i = 0 then []
  else if i = 1 then l
  else bigAdd l (mulByDigit (i-1) l)

