let bigMul l1 l2 = 
  let f a x =       (*XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX*)
    let (singleInt, largeInt) = x in
    let (multCount, oldMult) = a in 
    let miniResult = mulByDigit singleInt largeInt in
    let padResult = miniResult @ (clone 0 multCount) in (*XXXXXXXXXXXXXXXXXXXXXXXXXXXX*) 
    let finalResult = bigAdd padResult oldMult in
      (multCount + 1, finalResult)

  in
  let base = (0,[]) in   (*XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX*)
  let args = List.combine (List.rev l2) (clone l1 (List.length l2)) in
  let (_, res) = List.fold_left f base args in
    res


