    let base = (0, []) in
    let args = List.rev(List.combine (0::l1) (0::l2)) in  (*XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX*)
    let (_, res) = List.fold_left f base args in
      res
  in 
    removeZero (add (padZero l1 l2))
