let bigMulHelper singleInt largeInt multCount currResult =
  let tupleResult = mulByDigit singleInt largeInt in      (*XXXXXXXXXXXXXXXXX*)
  let paddedResult = tupleResult @ (clone 0 multCount) in (*XXXXXXXXXXXXXXXXX*)
    bigAdd paddedResult currResult			    (*XXXXXXXXXXXXXXXXXXXX*)
;;

let bigMul l1 l2 = 
  let f a x =       (*XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX*)
    let (singleInt, largeInt) = x in
    let (multCount, currResult) = a in 
    let finalResult = bigMulHelper singleInt largeInt multCount currResult in
      (multCount + 1, finalResult)
  in
  let base = (0,[]) in   (*XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX*)
  let args = List.combine (List.rev l2) (clone l1 (List.length l2)) in
  let (_, res) = List.fold_left f base args in
    res
;;

