let prefix130 = "130";;

type result =
  | Pass
  | Fail
  | ErrorCode of string;;

let print130 s = print_string (prefix130 ^ (">>" ^ s));;

let runWTimeout (f,arg,out,time) =
  try if (compare (f arg) out) = 0 then Pass else Fail
  with
  | e ->
      (print130 ("Uncaught Exception: " ^ (Printexc.to_string e));
       ErrorCode "exception");;

let max = ref 0;;

let score = ref 0;;

let scoreMsg () =
  Printf.sprintf "Results: Score/Max = %d / %d \n" (!score) (!max);;

