  | VarX  -> "x"
  | VarY  -> "y"
  | Sine e' -> "sin(pi*" ^ ((exprToString e') ^ ")")
  | Cosine e' -> "sin(pi*" ^ ((exprToString e') ^ ")")
  | Average (e1,e2) ->
      "(" ^ ((exprToString e1) ^ ("+" ^ ((exprToString e2) ^ ")/2")))
  | Times (e1,e2) ->
      "(" ^ ((exprToString e1) ^ ("*" ^ ((exprToString e2) ^ ")")))
  | Thresh (e1,e2,e3,e4) ->
      "(" ^
