let padZero l1 l2 =
  let len1 = List.length l1 in
  let len2 = List.length l2 in
  let shorter = 
    if len1 < len2 then l1
    else l2 in
  let zeros = 
    if shorter = l1 then (len2 - len1)
    else (len1 - len2) in
    if shorter = l1 then (List.append (clone 0 zeros) shorter, l2)
    else (l1, List.append (clone 0 zeros) shorter)


