  let f a x = 
    let getPos (p, l) = p in
    let pos = getPos a in
    let getProd (p, l) = l in
    let prod = getProd a in
      if pos > 0 
      then (pos+1, bigAdd (List.append (mulByDigit x l1) (clone 0 pos)) prod)
      else (1, bigAdd (mulByDigit x l1) prod) in
  let base = (0, [0]) in
  let args = List.rev l2 in
  let (_, res) = List.fold_left f base args in
    res



