let rec rev s =
  let n = String.length s in
  for i = 0 to (n - 1) / 2 do
    (let c = s.[i] in s.[i] <- s.[(n - i) - 1]; s.[(n - i) - 1] <- c)
  done;
  int_of_string s;;

let digitsOfInt n =
  if n < 0
  then []
  else
    (let n2 = rev (string_of_int n) in
     let rec reverse n3 =
       let x = n3 / 10
       and y = n3 mod 10 in
       if (x = 0) && (y = 0) then [] else y :: (reverse x) in
     reverse n2);;

