let padZero l1 l2 = 
  let first = List.length l1 in
  let sec = List.length l2 in
    if first < sec then
      List.append (clone 0 (sec - first)) l1, l2
    else if first > sec then
      l1, List.append (clone 0 (first - sec)) l2
    else 
      l1, l2
